CREATE TABLE "UTENTI" (
    "ID_UTENTE" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 99999999999 INCREMENT BY 1 START WITH 1 NOT NULL,
    "NOME" VARCHAR2(20) NOT NULL,
    "COGNOME" VARCHAR2(20) NOT NULL,
    "EMAIL" VARCHAR2(50) NOT NULL,
    "SESSO" NUMBER(1,0) NOT NULL,
    "DATA_DI_NASCITA" DATE NOT NULL,
    "TIPOLOGIA_TELEFONO" NUMBER(1,0) NOT NULL,
    "PREFISSO" NUMBER(3,0) NOT NULL,
    "NUMERO_TELEFONO" VARCHAR2(10) NOT NULL,
    "TIPOLOGIA_INDIRIZZO" NUMBER(1,0) NOT NULL,
    "INDIRIZZO" VARCHAR2(200) NOT NULL,
    "CAP" NUMBER(5,0) NOT NULL,
    "CITTA" VARCHAR2(20) NOT NULL,
    "PROVINCIA" VARCHAR2(20) NOT NULL,
    "NAZIONE" VARCHAR2(20) NOT NULL,
    "CODICE_FEDELTA" CHAR(10),
    CONSTRAINT "PK_ID_UTENTE" PRIMARY KEY ("ID_UTENTE")
); COMMIT;
