CREATE TABLE "VOLI" (
    "ID_VOLO" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 99999999999 INCREMENT BY 1 START WITH 1 NOT NULL,
    "CODICE_VOLO" CHAR(10) NOT NULL,
    "CITTA_PARTENZA" VARCHAR2(20) NOT NULL,
    "CITTA_DESTINAZIONE" VARCHAR2(20) NOT NULL,
    "DATA_VOLO" DATE NOT NULL,
    "PREZZO" NUMBER NOT NULL,
    "MAX_POSTI" NUMBER DEFAULT 100 NOT NULL,
    CONSTRAINT "PK_ID_VOLO" PRIMARY KEY ("ID_VOLO")
); COMMIT;
